bot.on('message', function(message) {
    getMessage = message.toString()
    if (message == "") return false;
    if (getMessage == "") return false;
    
    if (getMessage.includes("§§")) return false;
    if (getMessage.includes("<@")) return false;
    if (getMessage.includes("op_")) return false;
    if (getMessage.includes("www")) return false;
    if (getMessage.includes(".com")) return false;
    if (getMessage.includes(".org")) return false;
    if (getMessage.includes(".net")) return false;
    if (getMessage.includes("http")) return false;
    if (getMessage.includes("died")) return false;
    if (getMessage.includes("blown")) return false;
    if (getMessage.includes("slain")) return false;
    if (getMessage.includes("@here")) return false;
    if (getMessage.includes("killed")) return false;
    if (getMessage.includes("im dad")) return false;
    if (getMessage.includes("aternos")) return false;
    if (getMessage.includes("drowned")) return false;
    if (getMessage.includes("twitter")) return false;
    if (getMessage.includes("youtube")) return false;
    if (getMessage.includes("escaped")) return false;
    if (getMessage.includes("tried to")) return false
    if (getMessage.includes("muted for")) return false;
    if (getMessage.includes("fell from")) return false;
    if (getMessage.includes("burned to")) return false;
    if (getMessage.includes("@everyone")) return false;
    if (getMessage.includes("Teleported")) return false;
    if (getMessage.includes("suffocated")) return false;
    if (getMessage.includes("discord.gg/")) return false;
    if (getMessage.includes("from the game")) return false;
    if (getMessage.includes("left the game")) return false;
    if (getMessage.includes("was fireballed")) return false;
    if (getMessage.includes("joined the game")) return false;
    if (getMessage.includes("Reload complete.")) return false;
    if (getMessage.includes("If you encounter any")) return false;
    if (getMessage.includes("fell out of the world")) return false;
    if (getMessage.includes("Please note that this command")) return false;
    if (getMessage.includes("Use the /back command to return")) return false;

    if (getMessage.includes("I'm sorry, but you do not have permission")) {
        wait(1200)
        bot.chat("/op @p")
    }

    if (message == "Successfully disabled CommandSpy") {
        wait(1200)
        bot.chat("/cspy enable")
    }

    console.log(message.toString())
    client.channels.get("682659961033064552").send(message.toString())
})

bot.on('playerJoined', function(player, message) {
    if (configuration.includes('show-players-uuid = ["true"]') == true) {
        wait(1200)
        bot.chat("/title " + player.username + ' subtitle "Your UUID has been displayed on the server."')
        wait(1200)
        bot.chat("/title " + player.username + ' title "\\u00a7a\\u00a7l?"') 
        wait(1200)
        bot.chat("/title " + player.username + ' actionbar "\\u00a7f' + player.uuid + '"')
    }

    if (configuration.includes('deop-players-if-noop = ["true"]') == true) {
        if (isOperator(player.username)) {
            if (!isAdministrator(player.username)) {
                showOperator(player.username)
            }
        }
    }

    if (configuration.includes('deop-players-if-blocks = ["true"]') == true) {
        if (isBlocked(player.username)) {
            if (!isOperator(player.username)) {
                if (!isAdministrator(player.username)) {
                    showBlocked(player.username)
                }
            }
        }
    }

    // client.channels.get("608295668833976320").send(player.uuid + " - " + player.username + " joined the game")
})

